/* Microsoft 365 Card Hover Effects */
.microsoft-365-card {
    transition: all 0.3s ease-in-out;
    cursor: pointer;
    border-radius: 8px;
    padding: 20px;
    border: 1px solid #e0e0e0;
    background-color: #ffffff;
}

/* Also target WordPress blocks that might contain the card */
.wp-block-group.microsoft-365-card,
.wp-block-column.microsoft-365-card,
.wp-block-stack.microsoft-365-card {
    transition: all 0.3s ease-in-out;
    cursor: pointer;
    border-radius: 8px;
    padding: 20px !important;
    border: 1px solid #e0e0e0;
    background-color: #ffffff;
}

.microsoft-365-card:hover,
.wp-block-group.microsoft-365-card:hover,
.wp-block-column.microsoft-365-card:hover,
.wp-block-stack.microsoft-365-card:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.15);
    background-color: #f8f9fa;
    border-color: #0078d4;
}

.microsoft-365-card:hover h1,
.microsoft-365-card:hover h2,
.microsoft-365-card:hover h3,
.microsoft-365-card:hover h4,
.microsoft-365-card:hover .wp-block-heading {
    color: #0078d4 !important; /* Microsoft blue */
}

.microsoft-365-card:hover ul li,
.microsoft-365-card:hover .wp-block-list li {
    transform: translateX(5px);
    transition: transform 0.2s ease-in-out;
}
